package src.talento.a2020.m02.d13;

public class Pais {
    
	String sContinente,sZona,sNombre;
	public Pais() {
		// TODO Auto-generated constructor stub
	}
	public Pais(String sContinente, String sZona, String sNombre) {
		super();
		this.sContinente = sContinente;
		this.sZona = sZona;
		this.sNombre = sNombre;
	}
	public String getsContinente() {
		return sContinente;
	}
	public void setsContinente(String sContinente) {
		this.sContinente = sContinente;
	}
	public String getsZona() {
		return sZona;
	}
	public void setsZona(String sZona) {
		this.sZona = sZona;
	}
	public String getsNombre() {
		return sNombre;
	}
	public void setsNombre(String sNombre) {
		this.sNombre = sNombre;
	}

		
	

}
